import React, { Component } from 'react';
import ListGroup from 'react-bootstrap/ListGroup';
import Image from 'react-bootstrap/Image';
import Avatar from '../images/profile_avatar2.jpg';
import "../styles/person.css";
import { Row, Col } from 'react-bootstrap';


export class Person extends Component{
    constructor(props){
        super(props);
        this.state = {status:"online"};
    }

    componentDidMount(){
        /* get status from somewhere */
        let identifier = "dot" + this.props.name;
        if (this.state.status === "online"){
            document.getElementById(identifier).style.background = "green";
        }
        else if(this.state.status === "offline"){
            document.getElementById("dot").style.background = "red";
        }
        else if(this.state.status === "away"){
            document.getElementById("dot").style.background = "yellow";
        }
    }

    render(){
        let identifier = "dot" + this.props.name;
        return(
            <ListGroup.Item action variant="success" id="personLi" >
                <Row>
                    <Col>
                        <Image id="avatar" src={Avatar} roundedCircle />
                    </Col>
                    <Col id="profileName">
                        {this.props.name}
                    </Col>
                    <Col>
                        <span id={identifier}></span>
                    </Col>
                </Row>
            </ListGroup.Item>
        )
    }

}
