import React, { Component } from 'react';
import ListGroup from 'react-bootstrap/ListGroup';
import Image from 'react-bootstrap/Image';
import Avatar from '../images/profile_avatar2.jpg';
import "../styles/person.css";
import { Row, Col } from 'react-bootstrap';


export class Person extends Component{
    constructor(props){
        super(props);
        this.state = {status:""};
    }

    componentDidMount(){
        /* get status from database */
        let databaseStatus = "online";
        if (databaseStatus === "online"){
            document.getElementById("dot").style.background = "green";
            this.setState({status:"online"});
        }
        else if(databaseStatus === "offline"){
            document.getElementById("dot").style.background = "red";
            this.setState({status:"offline"});
        }
        else if(databaseStatus === "away"){
            document.getElementById("dot").style.background = "yellow";
            this.setState({status:"away"});
        }
    }

    componentDidUpdate(){
        console.log("HERE");
    }

    render(){
        let identifier = "dot" + this.props.name;
        return(
            <ListGroup.Item action variant="success" id="personLi" >
                <Row>
                    <Col>
                        <Image id="avatar" src={Avatar} roundedCircle />
                    </Col>
                    <Col id="profileName">
                        {this.props.name}
                    </Col>
                    <Col>
                        <span id="dot"></span>
                    </Col>
                </Row>
            </ListGroup.Item>
        )
    }

}
