import React, { useState, useEffect } from "react";
import { Line, Bar, Doughnut } from "react-chartjs-2";
import "../../styles/content.css";
import { Tab, Container, Row, Col, Nav, ToggleButtonGroup, ToggleButton} from "react-bootstrap";
import { Component } from "react";

class Content extends Component {
  constructor(props) {
    super(props);
    this.state = {
      charData: {},
      currGraph: ""
    };
  }

  componentDidMount() {
    // if (this.props.greeting === "line") {
    //   this.setState({ charData: setChartDataLine });
    // } else {
    //   this.setState({ charData: setChartDataOther });
    // }
    this.setState({currGraph: "line"});
  }

  renderSomething(randomArg) {
    <Tabs
      id="graph-buttons"
      variant={"pills"}
    >
      <Tab id="line-tab" eventKey="line" title="Line">
        <Content greeting={"line"} />
      </Tab>
      <Tab id="bar-tab" eventKey="otherline" title="OtherLine">
        <Content greeting={"bar"} />
      </Tab>
      <Tab id="doughnut-tab" eventKey="doughnut" title="Doughnut">
        <Content greeting={"doughnut"} />
      </Tab>
    </Tabs>
    if (randomArg === "line") {
      return (
        <div className="LineGraph">
          {
            <Line
              data={this.state.charData}
              options={charDataOptions}
              width={160}
              height={40}
            />
          }
        </div>
      );
    } else if (randomArg === "doughnut") {
      return (
        <div className="LineGraph">
          {<Doughnut data={this.state.charData} />}
        </div>
      );
    } else {
    }
    return <div>{<Bar data={this.state.charData} />}</div>;
  }

  render() {
    return this.renderSomething(this.props.greeting);
  }
}

const charDataOptions = {
  responsive: true,
  title: { text: "Hours spent during the last week", display: true },
  mainmaintainAspectRatio: false,
  scales: {
    yAxes: [
      {
        ticks: {
          autoSkip: true,
          maxTicksLimit: 10,
          beginAtZero: true,
        },
        gridLines: {
          display: false,
        },
      },
    ],
    xAxes: [
      {
        ticks: {
          autoSkip: true,
          maxTicksLimit: 10,
          beginAtZero: true,
        },
        gridLines: {
          display: false,
        },
      },
    ],
  },
};

const setChartDataLine = {
  labels: ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday"],
  datasets: [
    {
      label: "Hours Spent",
      lineTension: 0.5,
      data: [12, 5, 13, 18, 20],
      backgroundColor: "rgba(75, 192, 192, 0.6)",
      borderWidth: 2,
      borderColor: "rgba(0,0,0,1)",
    },
  ],
};

const setChartDataOther = {
  labels: ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday"],
  datasets: [
    {
      label: "Hours Spent",
      lineTension: 1,
      data: [12, 5, 13, 18, 20],
      backgroundColor: [
        "rgba(75, 192, 192, 0.6)",
        "red",
        "green",
        "blue",
        "purple",
        "maroon",
        "grey",
      ],
      borderWidth: 2,
      borderColor: "rgba(0,0,0,1)",
    },
  ],
};

export default Content;

