import React, {Component} from 'react';
import {Navbar, Nav, Form, FormControl, Button, NavDropdown, Image, NavItem} from 'react-bootstrap'
import {Person} from './Person';
import {Link, NavLink} from 'react-router-dom'
import Logo from '../../images/logo.png'
import Avatar from '../../images/profile_avatar2.jpg';
import "../../styles/navigation.css";
import {connect} from 'react-redux'
import {logout} from '../../store/actions/authActions';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/DropdownButton';
import {MenuItem} from 'react-bootstrap';
import DropdownItem from 'react-bootstrap/esm/DropdownItem';

class Navigation extends Component{
    constructor(props){
        super(props);
    }

    render(){
        const signedin = <>
                            <Dropdown>
                                <Dropdown.Toggle variant="success" id="dropdown-basic">
                                    <Image id="avatar" src={Avatar} roundedCircle />
                                </Dropdown.Toggle>

                                <Dropdown.Menu>
                                    <Dropdown.Item href="#/action-1">Action</Dropdown.Item>
                                    <Dropdown.Item href="#/action-2">Another action</Dropdown.Item>
                                    <Dropdown.Item href="#/action-3">Something else</Dropdown.Item>
                                </Dropdown.Menu>
                            </Dropdown>
                            <Nav.Link id="logout-btn" onClick={this.props.logout}>Logout</Nav.Link>
                            {/* <Image id="logo" src={Avatar} roundedCircle/> */}

                        </>;
        const signedout=<>  
                            <Nav.Link as={Link} to='/login' id="login-btn">Login</Nav.Link>
                            <Nav.Link as={Link} to='/register' id="register-btn">Sign-up</Nav.Link>

                        </>
        const checkLogin = this.props.auth.uid ? signedin : signedout

        return(
            <Navbar bg="light" expand="lg" id="navbar">
                <Navbar.Brand>
                    <Image id="logo" src={Logo} />
                </Navbar.Brand>
                <Navbar.Brand> 
                    <NavLink to="/" id="logo-text">Productivity App</NavLink>
                </Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="mr-auto">
                        {/* <Nav.Link href="">Home</Nav.Link>
                        <Nav.Link href="">Link</Nav.Link>
                        <NavDropdown title="Contact" id="basic-nav-dropdown">
                            <NavDropdown.Item> <Link to="/">Action</Link></NavDropdown.Item>
                            <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                            <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                            <NavDropdown.Divider />
                            <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
                        </NavDropdown> */}
                    </Nav>

                        <Nav>
                            <DropdownButton 
                            eventKey={3} 
                            title={
                                <Image id="avatar" src={Avatar} roundedCircle />
                            }
                            >
                            <DropdownItem eventKey='1'><i className="fa fa-envelope fa-fw"></i> User Profile</DropdownItem>
                            <DropdownItem eventKey='2'><i className="fa fa-gear fa-fw"></i> Settings</DropdownItem>
                            <DropdownItem divider />
                            <DropdownItem eventKey='3'><i className="fa fa-sign-out fa-fw"></i> Logout</DropdownItem>
                            </DropdownButton>
                        </Nav>

                </Navbar.Collapse>
            </Navbar>

        )
    }
}

const addStatetoProps = (state) => {
    console.log(state)
    return {
        auth: state.firebase.auth,
        profile: state.firebase.profile
    }
}

const addDispatchtoProps = (dispatch) => {
    return {
        logout: () => dispatch(logout())
    }
}


export default connect(addStatetoProps, addDispatchtoProps)(Navigation)
