const initialState = {
    authError: null
};

const authReducer = (state = initialState, action) => {
    if (action.type === 'LOGIN_SUCCESS'){
        console.log('login success')
        return {
            ...state,
            authError: null
        }

    }
    if (action.type === 'LOGIN_FAILED'){
        console.log('login failed');
        return {
            ...state,
            authError: "Login failed"
        }
    }
    return state;
}

export default authReducer
