import React, { useState, useEffect } from "react";
import { Line, Bar, Doughnut } from "react-chartjs-2";
import "../../styles/content.css";
import { Component } from "react";

class Content extends Component {
  constructor(props) {
    super(props);
    this.state = {
      charData: {},
    };
  }

  componentWillReceiveProps(nextProps) {
    console.log("HERe");
    this.setState({ setChartData });
  }

  render() {
    return (
      <div
        className="LineGraph"
        style={{ height: "50px", width: "1000px", margin: "400px" }}
      >
        <Doughnut data={this.state.charData} options={charDataOptions} />
      </div>
    );
  }
}

const charDataOptions = {
  responsive: true,
  title: { text: "Hours spent during the last week", display: true },
  scales: {
    yAxes: [
      {
        ticks: {
          autoSkip: true,
          maxTicksLimit: 10,
          beginAtZero: true,
        },
        gridLines: {
          display: false,
        },
      },
    ],
    xAxes: [
      {
        ticks: {
          autoSkip: true,
          maxTicksLimit: 10,
          beginAtZero: true,
        },
        gridLines: {
          display: false,
        },
      },
    ],
  },
};

const setChartData = {
  labels: ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday"],
  datasets: [
    {
      label: "Hours Spent",
      lineTension: 1,
      data: [3, 2, 1, 0, 8],
      backgroundColor: "rgba(75, 192, 192, 0.6)",
      borderWidth: 4,
    },
  ],
};

export default Content;

