import React, { Component } from 'react';
import ListGroup from 'react-bootstrap/ListGroup';
import Image from 'react-bootstrap/Image';
import Avatar from '../images/profile_avatar2.jpg';
import "../styles/person.css";
import { Row, Col } from 'react-bootstrap';
import { throwStatement } from '@babel/types';


export class Person extends Component{
    constructor(props){
        super(props);
        this.state = {status:""};
    }

    componentDidMount(){
        /* get status from database */
        // let identifier = this.props.name;
        // if (this.props.status === "online"){
        //     document.getElementById("dot-green").style.background = "green";
        //     this.setState({status:"online"});
        // }
        // else if(this.props.status === "offline"){
        //     document.getElementById("dot").style.background = "red";
        //     this.setState({status:"offline"});
        // }
        // else if(this.props.status === "away"){
        //     document.getElementById("dot").style.background = "yellow";
        //     this.setState({status:"away"});
        // }
    }

    render(){
        let color = 'green';
        if(this.props.status === 'offline'){
          color = 'red';
        } else if (this.props.status === 'away') {
          color = 'yellow';
        }

        return(
            <ListGroup.Item action variant="success" id="personLi" >
                <Row>
                    <Col>
                        <Image id="avatar" src={Avatar} roundedCircle />
                    </Col>
                    <Col id="profileName">
                        {this.props.name}
                    </Col>
                    <Col>
                        <span style={color}></span>
                    </Col>
                </Row>
            </ListGroup.Item>
        )
    }

}
