import React from "react";
import "./App.css";
//import 'bootstrap/dist/css/bootstrap.min.css';
import Navigation from "./components/layout/Navigation";
import FriendsBar from "./components/layout/FriendsBar";
import { BrowserRouter, Switch, Route } from "react-router-dom";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";
import { getFirebase } from "react-redux-firebase";

function App() {
  /* this is where we initial friends for user based from database for the logged in user*/
  console.log("HEREadsfasfadsfadsfadsfasd")
  const firebase = getFirebase();
  const firestore = firebase.firestore();
  const uid = firebase.auth().currentUser.uid;
  var userStatusDatabaseRef = firebase.database().ref('status/' + uid);
  var userStatusCloudRef = firestore.collection('users');


  return (
    <BrowserRouter>
      <div className="App">
        <Navigation />
        <Switch>
          <Route exact path="/" component={FriendsBar} />
          <Route path="/login" component={Login} />
          <Route path="/register" component={Register} />
        </Switch>
      </div>
    </BrowserRouter>
  );
}
// import { import } from '@babel/types';

export default App;
