import React, { Component } from 'react';
import "../styles/friendsBar.css";
import { Container } from 'react-bootstrap';
import { Row } from 'react-bootstrap';
import { Col } from 'react-bootstrap';
import { Button } from 'react-bootstrap';
import { Form } from 'react-bootstrap';
import {Person} from './Person';
import ListGroup from 'react-bootstrap/ListGroup';

export class FriendsBar extends Component {

    constructor(){
        super();
        this.state = {friends:[]};
    }

    componentDidMount() {
        /* initial friends from database when you login */
        const friends = ["Keerat", "Ishaan", "Harmeen"]
        this.setState({friends});
    }

    addFriend(name){
        const newList = this.state.friends.slice();
        newList.push(name)
        this.setState({friends:newList});
    }

    toggleFriendsBar() {
        document.getElementById('sidebar').classList.toggle('active');
    }

    toggleFriendsList(){
        document.getElementById('friendsList').classList.toggle('active');
    }

    render() {
        return (
            <Container id="sidebar">

                <div className="toggle" onClick={this.toggleFriendsBar}>
                    <span></span>
                    <span></span>
                    <span></span>
                </div>

                <Row>
                    <Col>
                        <p id="friendsText">Friends</p>
                    </Col>
                    <Col>
                        <Button id="addFriend" variant="dark" onClick={() => this.addFriend("Kee")}>+</Button>
                    </Col>
                </Row>

                <Row>
                    <Col>
                        <Form> 
                            <Form.Control id="searchFriend" placeholder="Find Friend..." onChange={this.toggleFriendList}/>
                        </Form>
                    </Col>
                </Row>

                <ListGroup >
                    <div id="friendList">
                        {this.state.friends.map((name) => {
                            return(
                                <Person name = {name} ></Person>
                            );
                        })}
                    </div>
                </ListGroup>


            </Container>
        )
    }
}
