import React, { Component } from 'react';
import "../styles/friendsBar.css";
import { Container } from 'react-bootstrap/Container';
import { Row } from 'react-bootstrap/Row';
import { Column } from 'react-bootstrap/Col';
import { Button } from 'react-bootstrap';
import {Person} from './Person';
import ListGroup from 'react-bootstrap/ListGroup';

export class FriendsBar extends Component {

    constructor(){
        super();
        this.state = {friends:["Harmeen", "Keerat", 'Ishaan']};
    }



    toggleFriendsBar() {
        document.getElementById('sidebar').classList.toggle('active');
    }

    render() {

        return (
            <div id="sidebar">
                <div className="toggle" onClick={this.toggleFriendsBar}>
                    <span></span>
                    <span></span>
                    <span></span>
                </div>
                <Button variant="dark" id="addFriend">+</Button>
                <ListGroup id="friendslist">
                    <div className="friends">
                        {/* <Person name="Harmeen"/>
                        <Person name="Ishaan"/>
                        <Person name="Keerat"/> */}
                        {friends.map((name) => {
                            return(
                                <Person name = {name} ></Person>
                            );
                        })}
                    </div>
                </ListGroup>
            </div>
        )
    }
}
